mapper.select:
  SELECT
	si.*,
	st.id type_id,
	st.NAME type_name,
	st2.id type_parent_id,
	st2.NAME type_parent_name,
	sz.size size,
	sc.colour colour,
	sc.colour_number colour_number,
	ui.user_name user_name,
	ui.photo_url photo_url
FROM
	social_img si
	LEFT JOIN social_hiapk_type st ON si.type_id = st.id
	LEFT JOIN social_hiapk_type st2 ON st2.id = st.parent_id
	LEFT JOIN social_hiapk_size sz ON si.size_id = sz.id
	LEFT JOIN social_hiapk_colour sc ON si.colour_id = sc.id
	LEFT JOIN user_info ui ON si.user_id = ui.id
where
    st2.status=1 and st.status = 1 and ui.status =1

      and  si.status != 5


      and si.status == #{status,jdbcType=VARCHAR}


      and si.ui == #{userId,jdbcType=VARCHAR}


      and si.charge == #{charge,jdbcType=VARCHAR}


      and st.typeId == #{typeId,jdbcType=VARCHAR}


      and sc.colour_id == #{colourId,jdbcType=VARCHAR}


      and sz.size_id == #{sizeId,jdbcType=VARCHAR}


      and si.created >= #{createdStart,jdbcType=VARCHAR}


      and si.created <= #{createdEnd,jdbcType=VARCHAR}


      and si.reduce_integral  > #{reduceIntegralStart,jdbcType=VARCHAR}


      and si.reduce_integral < #{reduceIntegralEnd,jdbcType=VARCHAR}

order by id desc
